jQuery(function($){
    $(document).ready(function(){
        $('.modal-active').click(function () {
            $('.modal, .side-menu-bg').addClass('active');
            $('.modal_menu').css({"left":"-1000px"});
        });
        $('.modal-active_registracia').click(function () {
            $('.modal_registracia, .side-menu-bg').addClass('active');
            $('.modal_menu').css({"left":"-1000px"});
        });
        $('.modal-close-btn').click(function () {
            $('.modal, .side-menu-bg').removeClass('active');
        });
        $('.modal-close-btn').click(function () {
            $('.modal_registracia, .side-menu-bg').removeClass('active');
        });
        $('.side-menu-bg').click(function () {
            $('.modal, .side-menu-bg').removeClass('active');
        });
        $('.side-menu-bg').click(function () {
            $('.modal_registracia, .side-menu-bg').removeClass('active');
        });
        document.getElementById('links').onclick = function (event) {
            event = event || window.event;
            var target = event.target || event.srcElement,
                link = target.src ? target.parentNode : target,
                options = {index: link, event: event},
                links = this.getElementsByTagName('a');
            blueimp.Gallery(links, options);
        };
    });
});
var side_menu_bg=document.getElementById('side_menu_bg');
var sandvich=document.getElementById('sandvich');
var modal_menu=document.getElementById('modal_menu');

function show() {
    modal_menu.style.left = "0px";
    side_menu_bg.classList.add('active')
}
sandvich.addEventListener('click',show);

function hide() {
    modal_menu.style.left = "-1000px";
    side_menu_bg.classList.remove('active')
}
side_menu_bg.addEventListener('click', hide);